// Variável para armazenar estado anterior
var estadoAnterior = context.get('estadoAnterior') || false;
var estadoAtual = msg.payload;

// Detecta MUDANÇA de estado (OFF → ON)
if (estadoAtual === true && estadoAnterior === false) {
    // Só executa quando muda de OFF para ON

    // Cria mensagem de alerta
    msg.payload = "⚠️ Cuidado. O acionamento está em modo REMOTO";
    msg.topic = " ALERTA!!! \nCMB 01 EM MODO REMOTO";

    // Salva o estado atual para próxima verificação
    context.set('estadoAnterior', estadoAtual);

    return msg;  // Envia apenas quando muda para ON
}
// Detecta MUDANÇA de estado (ON → OFF)
else if (estadoAtual === false && estadoAnterior === true) {
    // Opcional: mensagem quando desativa
    msg.payload = "✅ Modo REMOTO desativado. Sistema em LOCAL.";
    msg.topic = "MODO LOCAL";

    // Salva o estado atual
    context.set('estadoAnterior', estadoAtual);

    return msg;  // Envia quando muda para OFF
}
// Se não houve mudança (mantém no mesmo estado)
else {
    // Salva estado atual mesmo sem enviar mensagem
    context.set('estadoAnterior', estadoAtual);

    // NÃO retorna msg - não envia notificação
    return null;
}

var status, colorClass, text;

if (msg.payload === 1 || msg.payload === "on") {
    colorClass = "led-red";
    text = "CMB LIGADO";
} else {
    colorClass = "led-green";
    text = "CMB DESLIGADO";
}

// Montando a estrutura profissional
msg.payload = `
<div class="led-panel">
    <div class="led-socket">
        <div class="led-bulb ${colorClass}"></div>
    </div>
    <span class="led-status-text">${text}</span>
</div>
`;

return msg;

// Função para converter 0/1 em texto
if (msg.payload === 1) {
    msg.payload = "ON";
} else {
    msg.payload = "OFF";
}
return msg;

// imagem original (grande)
let original = msg.payload.clone();

// imagem para cálculo (já reduzida)
let atual = msg.payload;

let anterior = context.get("anterior");

// primeira execução
if (!anterior) {
    context.set("anterior", atual);
    context.set("ultima_original", original);
    return null;
}

// prepara diff (usa imagens pequenas)
msg.img1 = anterior;
msg.img2 = atual;

// guarda para próxima rodada
context.set("anterior", atual);
context.set("ultima_original", original);

// passa a ORIGINAL para envio
msg.original = original;

return msg;

/***********************
 * CONFIGURAÇÕES
 ***********************/
let LIMIAR = 0.01;          // sensibilidade do movimento
let INTERVALO = 15000;     // mínimo entre alertas (ms)
let PAUSA = 1000;         // pausa após detecção (ms)

/***********************
 * INICIALIZAÇÃO
 * (começa ATIVO automaticamente)
 ***********************/
if (context.get("ativo") === undefined) {
    context.set("ativo", true);
    context.set("ultimo", 0);
}

/***********************
 * VERIFICA SE ESTÁ EM PAUSA
 ***********************/
let ativo = context.get("ativo");
if (!ativo) {
    // sistema pausado: ignora tudo
    return null;
}

/***********************
 * VALOR DO DIFF
 ***********************/
let diff = msg.jobs[0].result;

/***********************
 * FILTRO DE MOVIMENTO
 ***********************/
if (diff < LIMIAR) {
    return null;
}

/***********************
 * ANTI-SPAM
 ***********************/
let agora = Date.now();
let ultimo = context.get("ultimo");

if (agora - ultimo < INTERVALO) {
    return null;
}

context.set("ultimo", agora);

/***********************
 * ENTRA EM PAUSA
 ***********************/
context.set("ativo", false);

setTimeout(() => {
    context.set("ativo", true);
    node.warn("Monitoramento reativado após pausa");
}, PAUSA);

/***********************
 * CONVERTE IMAGEM ORIGINAL PARA JPEG
 ***********************/
msg.payload = msg.original.getBufferAsync("image/jpeg");

/***********************
 * TEXTO DO TELEGRAM
 ***********************/
msg.caption =
    "🚨 Movimento detectado\n" +
    "Diferença: " + diff.toFixed(3) + "\n" +
    "⏸️ Pausa de " + (PAUSA / 1000) + " segundos";

/***********************
 * LIMPEZA DE MEMÓRIA
 ***********************/
delete msg.img1;
delete msg.img2;
delete msg.jobs;
delete msg.original;

/***********************
 * ENVIA PARA O TELEGRAM
 ***********************/
return msg;

// reduz drasticamente o uso de memória
msg.payload
    .resize(320,240)   // MUITO IMPORTANTE
    .grayscale();       // remove cores (menos dados)

return msg;

let habilitado = flow.get("inject_habilitado");

if (habilitado === true) {
    return msg;   // deixa passar
} else {
    return null;  // bloqueia
}
